<?php/** * Controller is the customized base controller class. * All controller classes for this application should extend from this base class. */class BackendController extends CController{    /**     * @var string the default layout for the controller view. Defaults to '//layouts/column1',     * meaning using a single column layout. See 'protected/views/layouts/siderbar.php'.     */    public $layout = '//layouts/content';    /**     * @var array context menu items. This property will be assigned to {@link CMenu::items}.     */    public $menu = array();    /**     * @var array the breadcrumbs of the current page. The value of this property will     * be assigned to {@link CBreadcrumbs::links}. Please refer to {@link CBreadcrumbs::links}     * for more details on how to specify this property.     */    public $breadcrumbs = array();    /**     * Displays a particular model.     * @param integer $id the ID of the model to be displayed     */    public function actionView($id)    {        $this->render('view',array(            'model'=>$this->loadModel($id),        ));    }}