<?phpclass FeedbackController extends Controller {    /**     * @var string the default layout for the views. Defaults to '//layouts/column2', meaning     * using two-column layout. See 'protected/views/layouts/column2.php'.     */    public $layout = 'column2';    /**     * @return array action filters     */    public function filters() {        return array(            'accessControl', // perform access control for CRUD operations        );    }    /**     * Specifies the access control rules.     * This method is used by the 'accessControl' filter.     * @return array access control rules     */    public function accessRules() {        return array(            array('allow', // allow all users to perform 'index' and 'view' actions                'actions' => array('index', 'view'),                'users' => array('*'),            ),            array('allow', // allow authenticated user to perform 'create' and 'update' actions                'actions' => array('create'),                'users' => array('@'),            ),            array('deny', // deny all users                'users' => array('*'),            ),        );    }    /**     * Displays a particular model.     * @param integer $id the ID of the model to be displayed     */    public function actionView($id) {        $this->render('view', array(            'model' => $this->loadModel($id),        ));    }    /**     * Creates a new model.     * If creation is successful, the browser will be redirected to the 'view' page.     */    public function actionCreate() {        $model = new Feedback;        // Uncomment the following line if AJAX validation is needed        // $this->performAjaxValidation($model);        if (isset($_POST['Feedback'])) {            $model->attributes = $_POST['Feedback'];            if ($model->save())                $this->redirect(array('view', 'id' => $model->id));        }        $this->render('create', array(            'model' => $model,        ));    }    /**     * Lists all models.     */    public function actionIndex() {        $model = new Feedback;        if (isset($_POST['Feedback'])) {            $model->attributes = $_POST['Feedback'];            if ($model->save())                $this->redirect(array('view', 'id' => $model->id));        }        $feedback = $model->findAll();        $this->render('index', array(            'feedback' => $feedback,            'model' => $model        ));    }    /**     * Returns the data model based on the primary key given in the GET variable.     * If the data model is not found, an HTTP exception will be raised.     * @param integer the ID of the model to be loaded     */    public function loadModel($id) {        $model = Feedback::model()->findByPk($id);        if ($model === null)            throw new CHttpException(404, 'The requested page does not exist.');        return $model;    }    /**     * Performs the AJAX validation.     * @param CModel the model to be validated     */    protected function performAjaxValidation($model) {        if (isset($_POST['ajax']) && $_POST['ajax'] === 'feedback-form') {            echo CActiveForm::validate($model);            Yii::app()->end();        }    }}