<?php$this->pageTitle = Yii::app()->name . ' - 留言反馈';$this->breadcrumbs = array(    '留言反馈',);?><div class="box">    <div class="box-title">留言反馈</div>    <div class="box-content">        <?php foreach ($feedback as $k => $f) { ?>            <ul>                <li style="margin-top:10px"><b>#<?php echo $k + 1 ?>楼</b>&nbsp;&nbsp;<?php echo $f->name ?>                    （<?php echo date('Y-m-d', $f->create_time) ?>）：                    <?php echo CHtml::link($f->content, array('/feedback/view', 'id' => $f->id)) ?>                    <p><?php if ($f->reply) { ?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;管理员回复：                            <span style="color:#cd0a0a"><?php echo $f->reply ?></span><?php } ?></p></li>            </ul>        <?php } ?>    </div>    <div class="box-title">发布留言</div>    <div class="box-content">        <ul>            <?php echo $this->renderPartial('_form', array('model' => $model)); ?>        </ul>    </div></div>